	#Gets the bill's name
	def getName(self):
		return self.name

	#Gets the bill's id tag
	def getId(self):
		return self.id

	#Gets the bill's HR tag
	def getHr(self):
		return self.hr

	#Gets the date on which the bill was voted.
	def getDate(self):
		return self.date

	#Gets the congress that voted on the bill.
	def getCongress(self):
		return self.congress

