#!/usr/bin/python
# -*- coding: utf-8 -*-

class Bill:
	#Constructor that builds a bill object given an ordered list of strings
	#generated by the database interface (this leaves vote_tally empty by
	#default)
	def __init__(self, id, params):
		self.id = params[0]
		self.session = params[1]
		self.roll = params[2]
		self.vote_date = params[3]
		self.type = params[4]
		self.question = params[5]
		if len(params)!=6:
			self.yea_votes = params[6]
			self.nay_votes = params[7]
			self.abstaining = params[8]
			self.absent = params[9]
		else:
			self.yea_votes = []
			self.nay_votes = []
			self.abstaining = []
			self.absent = []

	#Generates a link to the billâ€™s page with the properly formatted HTML text
	#and returns it as a string.
	def generateBillLink(self):
		HtmlString = ""
		return HtmlString

	def getId(self):
		return self.id

	def getSession(self):
		return self.session

	def getRoll(self):
		return self.roll

	def getVoteDate(self):
		return self.vote_date

	def getType(self):
		return self.type

	def getQuestion(self):
		return self.question

	#Returns the vote tally - a two-dimensional list of the votes; vote_tally[0]
	#is a list of senators, vote_tally[1] is a list of strings representing
	#votes: "Yea", "Nay", "Abstaining", or "Absent". For ease of creation, the
	#list is sorted by vote, in Y/N/Absent/Abstaining order.
	def getVoteTally(self):
		senator_list = []
		vote_list = []

		for senator in self.yea_votes:
			senator_list.append(senator)
			vote_list.append("Yea")
		for senator in self.nay_votes:
			senator_list.append(senator)
			vote_list.append("Nay")
		for senator in self.abstaining:
			senator_list.append(senator)
			vote_list.append("Abstaining")
		for senator in self.absent:
			senator_list.append(senator)
			vote_list.append("Absent")

		vote_tally = [senator_list, vote_list]
		return vote_tally