class Senator:
	#Constructor that builds a senator object from the senators id and an 
	#array of string parameters containing the variables from the senator as
	#generated by the database interface
	#birthday might be a date? ot
	def __init__(self, id_num, last, first, birthday, gender, party, wiki, current):
		this.id = id_num
		this.last = last
		this.first = first
		this.birthday = birthday
		this.gender = gender
		this.party = party
		this.wiki = wiki
		this.current = current #Boolean value, true means a current senator

	#Generates a link to the senatorâ€™s page with the properly formatted text and
	#returns it as a string(e.g. (R-AK) Richard White ).
	def generateHtmlNameLink(self):
		htmlLink = ""
		return htmlLink

	def getFirst(self):
		return this.first

	def getLast(self):
		return this.last

	def getName(self):
		return this.first + " " + this.last

	def getId(self):
		return this.id

	def getState(self):
		return this.state

	def getCongressesServed(self):
		return this.congresses_served

	def getParty(self):
		return this.party